import * as React from "react";
import { cn } from "@axis-ds/ui-react";

export interface AgentAvatarProps {
  /** 에이전트 이름 */
  name: string;
  /** 이미지 URL */
  src?: string;
  /** 크기 */
  size?: "sm" | "md" | "lg" | "xl";
  /** 상태 표시 */
  status?: "online" | "busy" | "offline";
  /** 에이전트 타입 */
  type?: "assistant" | "tool" | "system";
  /** 추가 클래스 */
  className?: string;
}

const sizeStyles = {
  sm: "w-6 h-6 text-xs",
  md: "w-8 h-8 text-sm",
  lg: "w-10 h-10 text-base",
  xl: "w-12 h-12 text-lg",
};

const statusStyles = {
  online: "bg-[var(--axis-color-green-500)]",
  busy: "bg-[var(--axis-color-yellow-500)]",
  offline: "bg-[var(--axis-color-gray-400)]",
};

const typeColors = {
  assistant: "bg-[var(--axis-color-purple-500)]",
  tool: "bg-[var(--axis-color-blue-500)]",
  system: "bg-[var(--axis-color-gray-600)]",
};

/**
 * 에이전트 아바타 컴포넌트
 */
export function AgentAvatar({
  name,
  src,
  size = "md",
  status,
  type = "assistant",
  className,
}: AgentAvatarProps) {
  const initials = name
    .split(" ")
    .map((n) => n[0])
    .join("")
    .slice(0, 2)
    .toUpperCase();

  return (
    <div className={cn("relative inline-flex", className)}>
      {src ? (
        <img
          src={src}
          alt={name}
          className={cn(
            "rounded-full object-cover",
            sizeStyles[size]
          )}
        />
      ) : (
        <div
          className={cn(
            "rounded-full flex items-center justify-center font-medium text-white",
            sizeStyles[size],
            typeColors[type]
          )}
        >
          {initials}
        </div>
      )}
      {status && (
        <span
          className={cn(
            "absolute bottom-0 right-0 rounded-full border-2 border-[var(--axis-surface-default)]",
            size === "sm" ? "w-2 h-2" : "w-3 h-3",
            statusStyles[status]
          )}
        />
      )}
    </div>
  );
}

import * as React from "react";
import { cn } from "@axis-ds/ui-react";

export type Severity = "info" | "warning" | "error";

export interface ApprovalAction {
  label: string;
  variant?: "default" | "secondary" | "ghost" | "destructive";
  onClick: () => void;
}

export interface ApprovalCardProps {
  /** 제목 */
  title: string;
  /** 설명 */
  description?: string;
  /** 심각도 (색상 결정) */
  severity?: Severity;
  /** 액션 버튼 목록 */
  actions: ApprovalAction[];
  /** 추가 내용 */
  children?: React.ReactNode;
  /** 추가 클래스 */
  className?: string;
}

const severityStyles: Record<Severity, { border: string; icon: string; bg: string }> = {
  info: {
    border: "border-l-[var(--axis-color-blue-500)]",
    icon: "text-[var(--axis-color-blue-500)]",
    bg: "bg-[var(--axis-surface-info)]",
  },
  warning: {
    border: "border-l-[var(--axis-color-yellow-500)]",
    icon: "text-[var(--axis-color-yellow-500)]",
    bg: "bg-[var(--axis-surface-warning)]",
  },
  error: {
    border: "border-l-[var(--axis-color-red-500)]",
    icon: "text-[var(--axis-color-red-500)]",
    bg: "bg-[var(--axis-surface-error)]",
  },
};

const severityIcons: Record<Severity, React.ReactNode> = {
  info: (
    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
    </svg>
  ),
  warning: (
    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
    </svg>
  ),
  error: (
    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
    </svg>
  ),
};

const actionVariantStyles: Record<string, string> = {
  default: "bg-[var(--axis-button-bg-default)] text-white hover:bg-[var(--axis-button-bg-hover)]",
  secondary: "bg-[var(--axis-button-secondary-bg-default)] text-[var(--axis-text-primary)] hover:bg-[var(--axis-button-secondary-bg-hover)]",
  ghost: "bg-transparent text-[var(--axis-text-primary)] hover:bg-[var(--axis-surface-secondary)]",
  destructive: "bg-[var(--axis-button-destructive-bg-default)] text-white hover:bg-[var(--axis-button-destructive-bg-hover)]",
};

/**
 * 사용자 승인이 필요한 작업을 표시하는 카드 컴포넌트
 */
export function ApprovalCard({
  title,
  description,
  severity = "info",
  actions,
  children,
  className,
}: ApprovalCardProps) {
  const styles = severityStyles[severity];

  return (
    <div
      className={cn(
        "rounded-lg border border-l-4 p-4",
        "bg-[var(--axis-surface-default)] border-[var(--axis-border-default)]",
        styles.border,
        className
      )}
    >
      <div className="flex items-start gap-3">
        <div className={cn("flex-shrink-0 mt-0.5", styles.icon)}>
          {severityIcons[severity]}
        </div>
        <div className="flex-1 min-w-0">
          <h4 className="text-sm font-semibold text-[var(--axis-text-primary)]">
            {title}
          </h4>
          {description && (
            <p className="mt-1 text-sm text-[var(--axis-text-secondary)]">
              {description}
            </p>
          )}
          {children && <div className="mt-3">{children}</div>}
          <div className="flex flex-wrap gap-2 mt-4">
            {actions.map((action, index) => (
              <button
                key={index}
                onClick={action.onClick}
                className={cn(
                  "px-3 py-1.5 text-sm font-medium rounded-md transition-colors",
                  actionVariantStyles[action.variant || "default"]
                )}
              >
                {action.label}
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

import * as React from "react";
import { cn } from "@axis-ds/ui-react";

export interface RecoveryBannerProps {
  /** 에러 메시지 */
  message: string;
  /** 상세 설명 */
  description?: string;
  /** 재시도 콜백 */
  onRetry?: () => void;
  /** 무시 콜백 */
  onDismiss?: () => void;
  /** 추가 클래스 */
  className?: string;
}

/**
 * 에러 발생 시 복구 옵션을 제공하는 배너
 */
export function RecoveryBanner({
  message,
  description,
  onRetry,
  onDismiss,
  className,
}: RecoveryBannerProps) {
  return (
    <div
      className={cn(
        "flex items-start gap-3 p-4 rounded-lg border",
        "bg-[var(--axis-surface-error)] border-[var(--axis-color-red-200)]",
        className
      )}
      role="alert"
    >
      <svg
        className="w-5 h-5 text-[var(--axis-color-red-500)] flex-shrink-0 mt-0.5"
        fill="none"
        stroke="currentColor"
        viewBox="0 0 24 24"
      >
        <path
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeWidth={2}
          d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z"
        />
      </svg>
      <div className="flex-1 min-w-0">
        <p className="text-sm font-medium text-[var(--axis-text-error)]">
          {message}
        </p>
        {description && (
          <p className="mt-1 text-sm text-[var(--axis-text-secondary)]">
            {description}
          </p>
        )}
        <div className="flex gap-2 mt-3">
          {onRetry && (
            <button
              onClick={onRetry}
              className="px-3 py-1.5 text-sm font-medium rounded-md bg-[var(--axis-color-red-500)] text-white hover:bg-[var(--axis-color-red-600)] transition-colors"
            >
              재시도
            </button>
          )}
          {onDismiss && (
            <button
              onClick={onDismiss}
              className="px-3 py-1.5 text-sm font-medium rounded-md bg-transparent text-[var(--axis-text-primary)] hover:bg-[var(--axis-surface-secondary)] transition-colors"
            >
              무시
            </button>
          )}
        </div>
      </div>
    </div>
  );
}

import * as React from "react";
import { cn } from "@axis-ds/ui-react";

export type StepStatus = "pending" | "running" | "complete" | "error";

export interface Step {
  id: string;
  label: string;
  status: StepStatus;
  description?: string;
}

export interface RunProgressProps {
  /** 전체 실행 상태 */
  status: "idle" | "running" | "complete" | "error";
  /** 단계 목록 */
  steps: Step[];
  /** 현재 진행 중인 단계 인덱스 */
  currentStep?: number;
  /** 취소 콜백 */
  onCancel?: () => void;
  /** 재시도 콜백 */
  onRetry?: () => void;
  /** 추가 클래스 */
  className?: string;
}

const statusColors: Record<StepStatus, string> = {
  pending: "bg-[var(--axis-color-gray-300)]",
  running: "bg-[var(--axis-color-blue-500)] animate-pulse",
  complete: "bg-[var(--axis-color-green-500)]",
  error: "bg-[var(--axis-color-red-500)]",
};

const statusIcons: Record<StepStatus, React.ReactNode> = {
  pending: null,
  running: (
    <svg className="w-3 h-3 animate-spin" viewBox="0 0 24 24" fill="none">
      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4z" />
    </svg>
  ),
  complete: (
    <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
    </svg>
  ),
  error: (
    <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
    </svg>
  ),
};

/**
 * 에이전트 실행 진행 상태를 표시하는 컴포넌트
 */
export function RunProgress({
  status,
  steps,
  currentStep,
  onCancel,
  onRetry,
  className,
}: RunProgressProps) {
  const progressPercent = React.useMemo(() => {
    const completed = steps.filter((s) => s.status === "complete").length;
    return Math.round((completed / steps.length) * 100);
  }, [steps]);

  return (
    <div
      className={cn(
        "rounded-lg border p-4",
        "bg-[var(--axis-surface-default)] border-[var(--axis-border-default)]",
        className
      )}
    >
      {/* 헤더 */}
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center gap-2">
          {status === "running" && (
            <div className="w-2 h-2 rounded-full bg-[var(--axis-color-blue-500)] animate-pulse" />
          )}
          <span className="text-sm font-medium text-[var(--axis-text-primary)]">
            {status === "idle" && "대기 중"}
            {status === "running" && "실행 중"}
            {status === "complete" && "완료"}
            {status === "error" && "오류 발생"}
          </span>
        </div>
        <span className="text-xs text-[var(--axis-text-secondary)]">
          {progressPercent}%
        </span>
      </div>

      {/* 진행 바 */}
      <div className="w-full h-1.5 rounded-full bg-[var(--axis-surface-secondary)] mb-4">
        <div
          className="h-full rounded-full bg-[var(--axis-color-blue-500)] transition-all duration-300"
          style={{ width: `${progressPercent}%` }}
        />
      </div>

      {/* 단계 목록 */}
      <div className="space-y-2">
        {steps.map((step, index) => (
          <div
            key={step.id}
            className={cn(
              "flex items-center gap-3 p-2 rounded-md transition-colors",
              currentStep === index && "bg-[var(--axis-surface-secondary)]"
            )}
          >
            <div
              className={cn(
                "w-5 h-5 rounded-full flex items-center justify-center text-white text-xs",
                statusColors[step.status]
              )}
            >
              {statusIcons[step.status] || index + 1}
            </div>
            <div className="flex-1 min-w-0">
              <p className="text-sm font-medium text-[var(--axis-text-primary)] truncate">
                {step.label}
              </p>
              {step.description && (
                <p className="text-xs text-[var(--axis-text-secondary)] truncate">
                  {step.description}
                </p>
              )}
            </div>
          </div>
        ))}
      </div>

      {/* 액션 버튼 */}
      {(onCancel || onRetry) && (
        <div className="flex justify-end gap-2 mt-4 pt-4 border-t border-[var(--axis-border-default)]">
          {status === "running" && onCancel && (
            <button
              onClick={onCancel}
              className="px-3 py-1.5 text-sm rounded-md bg-[var(--axis-surface-secondary)] text-[var(--axis-text-primary)] hover:bg-[var(--axis-surface-tertiary)]"
            >
              취소
            </button>
          )}
          {status === "error" && onRetry && (
            <button
              onClick={onRetry}
              className="px-3 py-1.5 text-sm rounded-md bg-[var(--axis-color-blue-500)] text-white hover:bg-[var(--axis-color-blue-600)]"
            >
              재시도
            </button>
          )}
        </div>
      )}
    </div>
  );
}

import * as React from "react";
import { cn } from "@axis-ds/ui-react";

export type SourceType = "web" | "file" | "database" | "api";

export interface Source {
  id: string;
  type: SourceType;
  title: string;
  url?: string;
  path?: string;
  snippet?: string;
  relevance?: number;
}

export interface SourcePanelProps {
  sources: Source[];
  expandable?: boolean;
  defaultExpanded?: boolean;
  maxItems?: number;
  className?: string;
}

const sourceIcons: Record<SourceType, React.ReactNode> = {
  web: (
    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 12a9 9 0 01-9 9m9-9a9 9 0 00-9-9m9 9H3m9 9a9 9 0 01-9-9m9 9c1.657 0 3-4.03 3-9s-1.343-9-3-9m0 18c-1.657 0-3-4.03-3-9s1.343-9 3-9m-9 9a9 9 0 019-9" />
    </svg>
  ),
  file: (
    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
    </svg>
  ),
  database: (
    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 7v10c0 2.21 3.582 4 8 4s8-1.79 8-4V7M4 7c0 2.21 3.582 4 8 4s8-1.79 8-4M4 7c0-2.21 3.582-4 8-4s8 1.79 8 4" />
    </svg>
  ),
  api: (
    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 9l3 3-3 3m5 0h3M5 20h14a2 2 0 002-2V6a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
    </svg>
  ),
};

/**
 * AI 응답의 출처/근거를 표시하는 패널
 */
export function SourcePanel({
  sources,
  expandable = true,
  defaultExpanded = false,
  maxItems = 5,
  className,
}: SourcePanelProps) {
  const [isExpanded, setIsExpanded] = React.useState(defaultExpanded);
  const displayedSources = isExpanded ? sources : sources.slice(0, maxItems);
  const hasMore = sources.length > maxItems;

  if (sources.length === 0) return null;

  return (
    <div
      className={cn(
        "rounded-lg border p-3",
        "bg-[var(--axis-surface-secondary)] border-[var(--axis-border-default)]",
        className
      )}
    >
      <h4 className="text-xs font-medium text-[var(--axis-text-secondary)] mb-2">
        출처 ({sources.length}개)
      </h4>
      <div className="space-y-2">
        {displayedSources.map((source) => (
          <div
            key={source.id}
            className="flex items-start gap-2 p-2 rounded-md bg-[var(--axis-surface-default)]"
          >
            <span className="text-[var(--axis-icon-secondary)] mt-0.5">
              {sourceIcons[source.type]}
            </span>
            <div className="flex-1 min-w-0">
              <p className="text-sm font-medium text-[var(--axis-text-primary)] truncate">
                {source.url ? (
                  <a
                    href={source.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="hover:underline"
                  >
                    {source.title}
                  </a>
                ) : (
                  source.title
                )}
              </p>
              {source.snippet && (
                <p className="text-xs text-[var(--axis-text-secondary)] line-clamp-2 mt-0.5">
                  {source.snippet}
                </p>
              )}
              {source.path && (
                <code className="text-xs text-[var(--axis-text-tertiary)]">
                  {source.path}
                </code>
              )}
            </div>
            {source.relevance !== undefined && (
              <span className="text-xs text-[var(--axis-text-tertiary)]">
                {Math.round(source.relevance * 100)}%
              </span>
            )}
          </div>
        ))}
      </div>
      {expandable && hasMore && (
        <button
          onClick={() => setIsExpanded(!isExpanded)}
          className="mt-2 text-xs text-[var(--axis-text-brand)] hover:underline"
        >
          {isExpanded ? "접기" : `${sources.length - maxItems}개 더 보기`}
        </button>
      )}
    </div>
  );
}

import * as React from "react";
import { cn } from "@axis-ds/ui-react";

export type TimelineStepStatus = "pending" | "running" | "complete" | "error" | "skipped";

export interface TimelineStep {
  id: string;
  label: string;
  status: TimelineStepStatus;
  timestamp?: Date;
  description?: string;
}

export interface StepTimelineProps {
  steps: TimelineStep[];
  orientation?: "vertical" | "horizontal";
  className?: string;
}

/**
 * 단계별 타임라인 컴포넌트
 */
export function StepTimeline({
  steps,
  orientation = "vertical",
  className,
}: StepTimelineProps) {
  const isVertical = orientation === "vertical";

  return (
    <div
      className={cn(
        isVertical ? "flex flex-col gap-4" : "flex items-center gap-2",
        className
      )}
    >
      {steps.map((step, index) => (
        <div
          key={step.id}
          className={cn(
            "relative flex",
            isVertical ? "items-start gap-3" : "flex-col items-center"
          )}
        >
          {/* 연결선 */}
          {index < steps.length - 1 && (
            <div
              className={cn(
                "absolute bg-[var(--axis-border-default)]",
                isVertical
                  ? "left-2.5 top-6 w-0.5 h-full -translate-x-1/2"
                  : "top-2.5 left-6 h-0.5 w-full"
              )}
            />
          )}

          {/* 상태 인디케이터 */}
          <div
            className={cn(
              "relative z-10 w-5 h-5 rounded-full flex items-center justify-center",
              step.status === "complete" && "bg-[var(--axis-color-green-500)]",
              step.status === "running" && "bg-[var(--axis-color-blue-500)] animate-pulse",
              step.status === "error" && "bg-[var(--axis-color-red-500)]",
              step.status === "skipped" && "bg-[var(--axis-color-gray-300)]",
              step.status === "pending" && "bg-[var(--axis-color-gray-300)]"
            )}
          >
            {step.status === "complete" && (
              <svg className="w-3 h-3 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M5 13l4 4L19 7" />
              </svg>
            )}
          </div>

          {/* 라벨 */}
          <div className={cn(isVertical ? "flex-1" : "text-center mt-2")}>
            <p className="text-sm font-medium text-[var(--axis-text-primary)]">
              {step.label}
            </p>
            {step.description && (
              <p className="text-xs text-[var(--axis-text-secondary)]">
                {step.description}
              </p>
            )}
            {step.timestamp && (
              <p className="text-xs text-[var(--axis-text-tertiary)]">
                {step.timestamp.toLocaleTimeString()}
              </p>
            )}
          </div>
        </div>
      ))}
    </div>
  );
}

import * as React from "react";
import { cn } from "@axis-ds/ui-react";

export interface StreamingTextProps {
  /** 스트리밍할 텍스트 */
  text: string;
  /** 스트리밍 속도 (ms) */
  speed?: number;
  /** 스트리밍 완료 여부 */
  isComplete?: boolean;
  /** 커서 표시 여부 */
  showCursor?: boolean;
  /** 완료 콜백 */
  onComplete?: () => void;
  /** 추가 클래스 */
  className?: string;
}

/**
 * 텍스트를 실시간으로 스트리밍하는 컴포넌트
 * AI 응답을 타이핑 효과로 표시할 때 사용
 */
export function StreamingText({
  text,
  speed = 20,
  isComplete = false,
  showCursor = true,
  onComplete,
  className,
}: StreamingTextProps) {
  const [displayedText, setDisplayedText] = React.useState("");
  const [isStreaming, setIsStreaming] = React.useState(false);
  const prevTextRef = React.useRef("");

  React.useEffect(() => {
    if (isComplete) {
      setDisplayedText(text);
      setIsStreaming(false);
      return;
    }

    // 새로운 텍스트가 추가된 경우에만 스트리밍
    if (text.length > prevTextRef.current.length && text.startsWith(prevTextRef.current)) {
      const newText = text.slice(prevTextRef.current.length);
      let charIndex = 0;
      setIsStreaming(true);

      const interval = setInterval(() => {
        if (charIndex < newText.length) {
          setDisplayedText((prev) => prev + newText[charIndex]);
          charIndex++;
        } else {
          clearInterval(interval);
          setIsStreaming(false);
          if (onComplete) onComplete();
        }
      }, speed);

      prevTextRef.current = text;
      return () => clearInterval(interval);
    } else if (text !== prevTextRef.current) {
      // 텍스트가 완전히 변경된 경우 리셋
      setDisplayedText("");
      prevTextRef.current = "";
    }
  }, [text, speed, isComplete, onComplete]);

  return (
    <div className={cn("relative", className)}>
      <span className="text-[var(--axis-text-primary)] whitespace-pre-wrap">
        {displayedText}
      </span>
      {showCursor && isStreaming && (
        <span className="inline-block w-0.5 h-4 ml-0.5 bg-[var(--axis-text-primary)] animate-pulse" />
      )}
    </div>
  );
}

import * as React from "react";
import { cn } from "@axis-ds/ui-react";

export type SurfaceType = "text" | "code" | "table" | "chart" | "form" | "custom";

export interface Surface {
  id: string;
  type: SurfaceType;
  title?: string;
  content: unknown;
  metadata?: Record<string, unknown>;
}

export interface SurfaceRendererProps {
  surface: Surface;
  renderCustom?: (surface: Surface) => React.ReactNode;
  className?: string;
}

/**
 * 다양한 타입의 Surface를 동적으로 렌더링하는 컴포넌트
 */
export function SurfaceRenderer({
  surface,
  renderCustom,
  className,
}: SurfaceRendererProps) {
  const renderContent = () => {
    switch (surface.type) {
      case "text":
        return (
          <div className="prose prose-sm max-w-none text-[var(--axis-text-primary)]">
            {String(surface.content)}
          </div>
        );

      case "code":
        return (
          <pre className="p-3 rounded-md bg-[var(--axis-surface-secondary)] overflow-x-auto">
            <code className="text-sm font-mono text-[var(--axis-text-primary)]">
              {String(surface.content)}
            </code>
          </pre>
        );

      case "table":
        const tableData = surface.content as { headers: string[]; rows: string[][] };
        return (
          <div className="overflow-x-auto">
            <table className="w-full text-sm">
              <thead>
                <tr className="border-b border-[var(--axis-border-default)]">
                  {tableData.headers.map((header, i) => (
                    <th
                      key={i}
                      className="px-3 py-2 text-left font-medium text-[var(--axis-text-secondary)]"
                    >
                      {header}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {tableData.rows.map((row, i) => (
                  <tr
                    key={i}
                    className="border-b border-[var(--axis-border-default)] last:border-0"
                  >
                    {row.map((cell, j) => (
                      <td
                        key={j}
                        className="px-3 py-2 text-[var(--axis-text-primary)]"
                      >
                        {cell}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        );

      case "chart":
        return (
          <div className="p-4 text-center text-[var(--axis-text-secondary)]">
            [차트: {surface.title || "데이터 시각화"}]
          </div>
        );

      case "form":
        return (
          <div className="p-4 text-center text-[var(--axis-text-secondary)]">
            [폼: {surface.title || "입력 양식"}]
          </div>
        );

      case "custom":
        if (renderCustom) {
          return renderCustom(surface);
        }
        return (
          <div className="p-4 text-center text-[var(--axis-text-secondary)]">
            [커스텀 Surface]
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div
      className={cn(
        "rounded-lg border p-4",
        "bg-[var(--axis-surface-default)] border-[var(--axis-border-default)]",
        className
      )}
    >
      {surface.title && (
        <h4 className="text-sm font-medium text-[var(--axis-text-primary)] mb-3">
          {surface.title}
        </h4>
      )}
      {renderContent()}
    </div>
  );
}

