import * as React from "react";
import { cn } from "@axis-ds/ui-react";

export type ToolStatus = "pending" | "running" | "success" | "error";

export interface ToolCallCardProps {
  /** 도구 이름 */
  toolName: string;
  /** 도구 설명 */
