'use client'

import { TrendingUp, TrendingDown } from 'lucide-react'
import { cn } from '../lib/utils'
import type { KpiItem } from '../lib/demo-data'

const zoneBarColor: Record<KpiItem['zone'], string> = {
  ingest: 'bg-zone-ingest',
  struct: 'bg-zone-struct',
  graph: 'bg-zone-graph',
  path: 'bg-zone-path',
}

interface KpiCardGridProps {
  items: KpiItem[]
}

export function KpiCardGrid({ items }: KpiCardGridProps) {
  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
      {items.map((item, i) => (
        <div
          key={item.label}
          className={cn(
            'glass-panel relative overflow-hidden p-5 opacity-0 animate-fade-in-up',
            i === 0 && 'stagger-1',
            i === 1 && 'stagger-2',
            i === 2 && 'stagger-3',
            i === 3 && 'stagger-4'
          )}
        >
          {/* Zone accent bar */}
          <div className={cn('absolute top-0 left-0 right-0 h-1', zoneBarColor[item.zone])} />

          <p className="text-xs text-muted-foreground mb-1">{item.label}</p>
          <p className="text-2xl font-bold tracking-tight">{item.value}</p>

          <div className="mt-2 flex items-center gap-1 text-xs">
            {item.change >= 0 ? (
              <>
                <TrendingUp className="h-3 w-3 text-chart-success" />
                <span className="text-chart-success">+{item.change}%</span>
              </>
            ) : (
              <>
                <TrendingDown className="h-3 w-3 text-chart-alert" />
                <span className="text-chart-alert">{item.change}%</span>
              </>
            )}
            <span className="text-muted-foreground">전월 대비</span>
          </div>
        </div>
      ))}
    </div>
  )
}

'use client'

import { useState } from 'react'
import type { ProjectStatus } from '../lib/demo-data'

interface ProjectStatusChartProps {
  data: ProjectStatus[]
}

export function ProjectStatusChart({ data }: ProjectStatusChartProps) {
  const total = data.reduce((sum, d) => sum + d.value, 0)
  const [hoveredIndex, setHoveredIndex] = useState<number | null>(null)

  // SVG 도넛 계산
  const cx = 80
  const cy = 80
  const r = 60
  const circumference = 2 * Math.PI * r

  let accumulated = 0
  const arcs = data.map((d) => {
    const ratio = d.value / total
    const dashLength = ratio * circumference
    const dashOffset = circumference - accumulated * circumference
    accumulated += ratio
    return { ...d, dashLength, dashOffset, ratio }
  })

  const centerLabel = hoveredIndex !== null ? data[hoveredIndex] : null

  return (
    <div className="glass-panel p-5">
      <h3 className="text-sm font-semibold mb-4">프로젝트 현황</h3>

      <div className="flex items-center gap-6">
        {/* 도넛 */}
        <svg
          viewBox="0 0 160 160"
          className="w-36 h-36 flex-shrink-0"
        >
          {arcs.map((arc, i) => (
            <circle
              key={arc.label}
              cx={cx}
              cy={cy}
              r={r}
              fill="none"
              stroke={arc.color}
              strokeWidth={hoveredIndex === i ? 22 : 18}
              strokeDasharray={`${arc.dashLength} ${circumference - arc.dashLength}`}
              strokeDashoffset={arc.dashOffset}
              transform={`rotate(-90 ${cx} ${cy})`}
              className="transition-all duration-200 cursor-pointer"
              onMouseEnter={() => setHoveredIndex(i)}
              onMouseLeave={() => setHoveredIndex(null)}
            />
          ))}
          {/* 중앙 텍스트 */}
          <text x={cx} y={cy - 6} textAnchor="middle" className="fill-foreground text-2xl font-bold" fontSize="22">
            {centerLabel ? centerLabel.value : total}
          </text>
          <text x={cx} y={cy + 14} textAnchor="middle" className="fill-muted-foreground text-[10px]" fontSize="10">
            {centerLabel ? centerLabel.label : '전체'}
          </text>
        </svg>

        {/* 범례 */}
        <div className="flex flex-col gap-2 text-sm">
          {data.map((d, i) => (
            <div
              key={d.label}
              className="flex items-center gap-2 cursor-pointer"
              onMouseEnter={() => setHoveredIndex(i)}
              onMouseLeave={() => setHoveredIndex(null)}
            >
              <span
                className="inline-block w-3 h-3 rounded-sm flex-shrink-0"
                style={{ backgroundColor: d.color }}
              />
              <span className="text-muted-foreground">{d.label}</span>
              <span className="font-semibold ml-auto">{d.value}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  )
}

'use client'

import { useState, useMemo } from 'react'
import type { SkillNode } from '../lib/demo-data'

interface SkillTreemapProps {
  data: SkillNode[]
}

interface TreemapRect {
  x: number
  y: number
  w: number
  h: number
  node: SkillNode
}

/** Squarified treemap 알고리즘 (단순화 버전) */
function squarify(nodes: SkillNode[], x: number, y: number, w: number, h: number): TreemapRect[] {
  const total = nodes.reduce((s, n) => s + n.value, 0)
  if (total === 0 || nodes.length === 0) return []

  const rects: TreemapRect[] = []
  let cx = x
  let cy = y
  let cw = w
  let ch = h
  const sorted = [...nodes].sort((a, b) => b.value - a.value)

  let remaining = total

  let i = 0
  while (i < sorted.length) {
    const isHorizontal = cw >= ch
    const side = isHorizontal ? ch : cw

    // 한 행(row)에 들어갈 노드 모음
    const row: SkillNode[] = [sorted[i]]
    let rowSum = sorted[i].value
    let bestAspect = Infinity

    for (let j = i + 1; j < sorted.length; j++) {
      const testSum = rowSum + sorted[j].value
      const fraction = testSum / remaining
      const rowLength = isHorizontal ? cw * fraction : ch * fraction

      // worst aspect ratio 계산
      let worstAspect = 0
      let accum = 0
      for (const item of [...row, sorted[j]]) {
        accum += item.value
        const itemFraction = item.value / testSum
        const itemLen = side * itemFraction
        const aspect = Math.max(rowLength / itemLen, itemLen / rowLength)
        worstAspect = Math.max(worstAspect, aspect)
      }

      if (row.length === 1) {
        bestAspect = worstAspect
        row.push(sorted[j])
        rowSum = testSum
      } else if (worstAspect <= bestAspect) {
        bestAspect = worstAspect
        row.push(sorted[j])
        rowSum = testSum
      } else {
        break
      }
    }

    // 행 배치
    const fraction = rowSum / remaining
    const rowLength = isHorizontal ? cw * fraction : ch * fraction

    let offset = 0
    for (const item of row) {
      const itemFraction = item.value / rowSum
      const itemLen = side * itemFraction

      if (isHorizontal) {
        rects.push({ x: cx, y: cy + offset, w: rowLength, h: itemLen, node: item })
      } else {
        rects.push({ x: cx + offset, y: cy, w: itemLen, h: rowLength, node: item })
      }
      offset += itemLen
    }

    // 남은 공간 갱신
    if (isHorizontal) {
      cx += rowLength
      cw -= rowLength
    } else {
      cy += rowLength
      ch -= rowLength
    }
    remaining -= rowSum
    i += row.length
  }

  return rects
}

export function SkillTreemap({ data }: SkillTreemapProps) {
  const [hovered, setHovered] = useState<string | null>(null)

  const W = 480
  const H = 280
  const rects = useMemo(() => squarify(data, 0, 0, W, H), [data])

  return (
    <div className="glass-panel p-5">
      <h3 className="text-sm font-semibold mb-4">스킬 분포</h3>

      <svg viewBox={`0 0 ${W} ${H}`} className="w-full rounded-md overflow-hidden">
        {rects.map((rect) => {
          const isHovered = hovered === rect.node.name
          return (
            <g
              key={rect.node.name}
              onMouseEnter={() => setHovered(rect.node.name)}
              onMouseLeave={() => setHovered(null)}
              className="cursor-pointer"
            >
              <rect
                x={rect.x + 1}
                y={rect.y + 1}
                width={Math.max(0, rect.w - 2)}
                height={Math.max(0, rect.h - 2)}
                rx={4}
                fill={rect.node.color}
                opacity={isHovered ? 1 : 0.75}
                className="transition-opacity duration-150"
              />
              {rect.w > 40 && rect.h > 28 && (
                <>
                  <text
                    x={rect.x + rect.w / 2}
                    y={rect.y + rect.h / 2 - 4}
                    textAnchor="middle"
                    fill="white"
                    fontSize={rect.w > 80 ? 13 : 10}
                    fontWeight="600"
                  >
                    {rect.node.name}
                  </text>
                  <text
                    x={rect.x + rect.w / 2}
                    y={rect.y + rect.h / 2 + 12}
                    textAnchor="middle"
                    fill="white"
                    fontSize={9}
                    opacity={0.8}
                  >
                    {rect.node.value}명
                  </text>
                </>
              )}
            </g>
          )
        })}
      </svg>
    </div>
  )
}

'use client'

import { BarChart3, Users, TrendingUp } from 'lucide-react'
import { cn } from '../lib/utils'

export type TabId = 'resource' | 'talent' | 'forecast'

interface Tab {
  id: TabId
  label: string
  icon: React.ReactNode
}

const tabs: Tab[] = [
  { id: 'resource', label: '자원 배분', icon: <BarChart3 className="h-4 w-4" /> },
  { id: 'talent', label: '인재 정보', icon: <Users className="h-4 w-4" /> },
  { id: 'forecast', label: '인력 예측', icon: <TrendingUp className="h-4 w-4" /> },
]

interface TabNavigationProps {
  activeTab: TabId
  onTabChange: (tab: TabId) => void
}

export function TabNavigation({ activeTab, onTabChange }: TabNavigationProps) {
  return (
    <nav className="flex gap-1 border-b border-border">
      {tabs.map((tab) => (
        <button
          key={tab.id}
          onClick={() => onTabChange(tab.id)}
          className={cn(
            'flex items-center gap-2 px-4 py-3 text-sm font-medium transition-colors relative',
            activeTab === tab.id
              ? 'text-foreground'
              : 'text-muted-foreground hover:text-foreground'
          )}
        >
          {tab.icon}
          {tab.label}
          {activeTab === tab.id && (
            <span className="absolute bottom-0 left-0 right-0 h-0.5 bg-primary rounded-full" />
          )}
        </button>
      ))}
    </nav>
  )
}

'use client'

import { useState } from 'react'
import { ArrowUpDown } from 'lucide-react'
import { cn } from '../lib/utils'
import { type TalentRow, departments } from '../lib/demo-data'

interface TalentTableProps {
  data: TalentRow[]
}

type SortKey = 'name' | 'experience' | 'rating'
type SortDir = 'asc' | 'desc'

const ratingOrder: Record<string, number> = { S: 4, A: 3, B: 2, C: 1 }
const ratingColor: Record<string, string> = {
  S: 'bg-chart-success/20 text-chart-success',
  A: 'bg-chart-blue/20 text-chart-blue',
  B: 'bg-chart-warning/20 text-chart-warning',
  C: 'bg-chart-alert/20 text-chart-alert',
}
const riskColor: Record<string, string> = {
  low: 'text-chart-success',
  mid: 'text-chart-warning',
  high: 'text-chart-alert',
}
const riskLabel: Record<string, string> = { low: '낮음', mid: '보통', high: '높음' }

export function TalentTable({ data }: TalentTableProps) {
  const [dept, setDept] = useState('전체')
  const [sortKey, setSortKey] = useState<SortKey>('rating')
  const [sortDir, setSortDir] = useState<SortDir>('desc')

  const toggle = (key: SortKey) => {
    if (sortKey === key) {
      setSortDir((d) => (d === 'asc' ? 'desc' : 'asc'))
    } else {
      setSortKey(key)
      setSortDir('desc')
    }
  }

  const filtered = dept === '전체' ? data : data.filter((r) => r.department === dept)
  const sorted = [...filtered].sort((a, b) => {
    let cmp = 0
    if (sortKey === 'name') cmp = a.name.localeCompare(b.name)
    else if (sortKey === 'experience') cmp = a.experience - b.experience
    else cmp = (ratingOrder[a.rating] ?? 0) - (ratingOrder[b.rating] ?? 0)
    return sortDir === 'asc' ? cmp : -cmp
  })

  return (
    <div className="glass-panel p-5">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-sm font-semibold">인재 현황</h3>
        <select
          value={dept}
          onChange={(e) => setDept(e.target.value)}
          className="text-xs bg-secondary text-secondary-foreground rounded-md px-2 py-1 border border-border"
        >
          {departments.map((d) => (
            <option key={d} value={d}>{d}</option>
          ))}
        </select>
      </div>

      <div className="overflow-x-auto">
        <table className="w-full text-sm">
          <thead>
            <tr className="border-b border-border text-left">
              <th className="pb-2 font-medium text-muted-foreground">ID</th>
              <th className="pb-2 font-medium text-muted-foreground cursor-pointer select-none" onClick={() => toggle('name')}>
                <span className="inline-flex items-center gap-1">이름 <ArrowUpDown className="h-3 w-3" /></span>
              </th>
              <th className="pb-2 font-medium text-muted-foreground">부서</th>
              <th className="pb-2 font-medium text-muted-foreground">역할</th>
              <th className="pb-2 font-medium text-muted-foreground">스킬</th>
              <th className="pb-2 font-medium text-muted-foreground cursor-pointer select-none" onClick={() => toggle('experience')}>
                <span className="inline-flex items-center gap-1">경력 <ArrowUpDown className="h-3 w-3" /></span>
              </th>
              <th className="pb-2 font-medium text-muted-foreground cursor-pointer select-none" onClick={() => toggle('rating')}>
                <span className="inline-flex items-center gap-1">평가 <ArrowUpDown className="h-3 w-3" /></span>
              </th>
              <th className="pb-2 font-medium text-muted-foreground">이직위험</th>
            </tr>
          </thead>
          <tbody>
            {sorted.map((row) => (
              <tr key={row.id} className="border-b border-border/50 hover:bg-accent/30 transition-colors">
                <td className="py-2.5 text-muted-foreground">{row.id}</td>
                <td className="py-2.5 font-medium">{row.name}</td>
                <td className="py-2.5 text-muted-foreground">{row.department}</td>
                <td className="py-2.5 text-muted-foreground">{row.role}</td>
                <td className="py-2.5">
                  <div className="flex gap-1 flex-wrap">
                    {row.skills.map((s) => (
                      <span key={s} className="px-1.5 py-0.5 rounded bg-secondary text-[11px]">{s}</span>
                    ))}
                  </div>
                </td>
                <td className="py-2.5">{row.experience}년</td>
                <td className="py-2.5">
                  <span className={cn('px-2 py-0.5 rounded-full text-xs font-semibold', ratingColor[row.rating])}>
                    {row.rating}
                  </span>
                </td>
                <td className={cn('py-2.5 text-xs font-medium', riskColor[row.riskLevel])}>
                  {riskLabel[row.riskLevel]}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  )
}

'use client'

import { useState } from 'react'
import type { WorkforceFlowMonth } from '../lib/demo-data'

interface WorkforceFlowChartProps {
  data: WorkforceFlowMonth[]
}

export function WorkforceFlowChart({ data }: WorkforceFlowChartProps) {
  const [hoveredIdx, setHoveredIdx] = useState<number | null>(null)

  // 차트 영역 크기
  const W = 720
  const H = 320
  const padL = 50
  const padR = 50
  const padT = 20
  const padB = 40
  const chartW = W - padL - padR
  const chartH = H - padT - padB

  // 바 스케일 (유입/유출)
  const maxBar = Math.max(...data.map((d) => Math.max(d.inflow, d.outflow)))
  const barScale = chartH / (maxBar * 1.3)
  const barW = chartW / data.length * 0.3
  const gap = chartW / data.length

  // 라인 스케일 (인원수)
  const allHeadcounts = data.flatMap((d) => [d.actualHeadcount, d.predictedHeadcount].filter((v): v is number => v !== null && v > 0))
  const minHC = Math.min(...allHeadcounts) - 10
  const maxHC = Math.max(...allHeadcounts) + 10
  const hcRange = maxHC - minHC || 1

  function hcY(v: number) {
    return padT + chartH - ((v - minHC) / hcRange) * chartH
  }

  // 실제 라인
  const actualPoints = data
    .filter((d) => d.actualHeadcount > 0)
    .map((d, _i) => {
      const idx = data.indexOf(d)
      return `${padL + idx * gap + gap / 2},${hcY(d.actualHeadcount)}`
    })
    .join(' ')

  // 예측 라인
  const predictedPoints = data
    .filter((d) => d.predictedHeadcount !== null)
    .map((d) => {
      const idx = data.indexOf(d)
      return `${padL + idx * gap + gap / 2},${hcY(d.predictedHeadcount!)}`
    })
    .join(' ')

  return (
    <div className="glass-panel p-5">
      <h3 className="text-sm font-semibold mb-4">인력 유입/유출 및 인원 추이</h3>

      <svg viewBox={`0 0 ${W} ${H}`} className="w-full">
        {/* Y축 눈금 (바) */}
        {[0, Math.round(maxBar / 2), maxBar].map((v) => {
          const y = padT + chartH - v * barScale
          return (
            <g key={`bar-y-${v}`}>
              <line x1={padL} y1={y} x2={padL + chartW} y2={y} stroke="hsl(var(--border))" strokeDasharray="4" />
              <text x={padL - 6} y={y + 4} textAnchor="end" className="fill-muted-foreground" fontSize="10">{v}</text>
            </g>
          )
        })}

        {/* Y축 눈금 (라인) */}
        {[minHC, Math.round((minHC + maxHC) / 2), maxHC].map((v) => (
          <text key={`hc-y-${v}`} x={padL + chartW + 6} y={hcY(v) + 4} textAnchor="start" className="fill-muted-foreground" fontSize="10">
            {v}
          </text>
        ))}

        {/* 바 */}
        {data.map((d, i) => {
          const x = padL + i * gap + gap / 2
          return (
            <g
              key={d.month}
              onMouseEnter={() => setHoveredIdx(i)}
              onMouseLeave={() => setHoveredIdx(null)}
              className="cursor-pointer"
            >
              {/* 유입 바 */}
              {d.inflow > 0 && (
                <rect
                  x={x - barW - 1}
                  y={padT + chartH - d.inflow * barScale}
                  width={barW}
                  height={d.inflow * barScale}
                  rx={2}
                  fill="hsl(var(--chart-blue))"
                  opacity={hoveredIdx === i ? 1 : 0.7}
                  className="transition-opacity"
                />
              )}
              {/* 유출 바 */}
              {d.outflow > 0 && (
                <rect
                  x={x + 1}
                  y={padT + chartH - d.outflow * barScale}
                  width={barW}
                  height={d.outflow * barScale}
                  rx={2}
                  fill="hsl(var(--chart-alert))"
                  opacity={hoveredIdx === i ? 1 : 0.7}
                  className="transition-opacity"
                />
              )}
              {/* X축 레이블 */}
              <text x={x} y={H - padB + 16} textAnchor="middle" className="fill-muted-foreground" fontSize="11">
                {d.month}
              </text>
            </g>
          )
        })}

        {/* 실제 라인 */}
        <polyline
          points={actualPoints}
          fill="none"
          stroke="hsl(var(--chart-success))"
          strokeWidth={2}
          strokeLinejoin="round"
        />
        {data.filter((d) => d.actualHeadcount > 0).map((d) => {
          const idx = data.indexOf(d)
          return (
            <circle
              key={`act-${d.month}`}
              cx={padL + idx * gap + gap / 2}
              cy={hcY(d.actualHeadcount)}
              r={3}
              fill="hsl(var(--chart-success))"
            />
          )
        })}

        {/* 예측 라인 (점선) */}
        <polyline
          points={predictedPoints}
          fill="none"
          stroke="hsl(var(--chart-warning))"
          strokeWidth={2}
          strokeDasharray="6 3"
          strokeLinejoin="round"
        />
        {data.filter((d) => d.predictedHeadcount !== null).map((d) => {
          const idx = data.indexOf(d)
          return (
            <circle
              key={`pred-${d.month}`}
              cx={padL + idx * gap + gap / 2}
              cy={hcY(d.predictedHeadcount!)}
              r={3}
              fill="hsl(var(--chart-warning))"
              strokeWidth={1}
              stroke="hsl(var(--background))"
            />
          )
        })}

        {/* 호버 툴팁 */}
        {hoveredIdx !== null && data[hoveredIdx] && (
          (() => {
            const d = data[hoveredIdx]
            const x = padL + hoveredIdx * gap + gap / 2
            return (
              <g>
                <line x1={x} y1={padT} x2={x} y2={padT + chartH} stroke="hsl(var(--foreground))" strokeOpacity={0.15} />
                <rect x={x - 48} y={padT - 2} width={96} height={38} rx={4} fill="hsl(var(--card))" stroke="hsl(var(--border))" />
                <text x={x} y={padT + 12} textAnchor="middle" className="fill-foreground" fontSize="10" fontWeight="600">
                  유입 {d.inflow} / 유출 {d.outflow}
                </text>
                {(d.actualHeadcount > 0 || d.predictedHeadcount !== null) && (
                  <text x={x} y={padT + 26} textAnchor="middle" className="fill-muted-foreground" fontSize="10">
                    인원 {d.actualHeadcount > 0 ? d.actualHeadcount : d.predictedHeadcount}
                  </text>
                )}
              </g>
            )
          })()
        )}
      </svg>

      {/* 범례 */}
      <div className="flex gap-5 mt-3 text-xs text-muted-foreground justify-center">
        <span className="flex items-center gap-1.5"><span className="w-3 h-0.5 bg-[hsl(217,91%,60%)] inline-block" /> 유입</span>
        <span className="flex items-center gap-1.5"><span className="w-3 h-0.5 bg-[hsl(0,72%,51%)] inline-block" /> 유출</span>
        <span className="flex items-center gap-1.5"><span className="w-3 h-0.5 bg-[hsl(142,71%,45%)] inline-block" /> 실제 인원</span>
        <span className="flex items-center gap-1.5"><span className="w-3 h-0.5 bg-[hsl(38,92%,50%)] inline-block border-dashed" /> 예측 인원</span>
      </div>
    </div>
  )
}

@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 0 0% 100%;
    --foreground: 240 10% 3.9%;
    --card: 0 0% 100%;
    --card-foreground: 240 10% 3.9%;
    --popover: 0 0% 100%;
    --popover-foreground: 240 10% 3.9%;
    --primary: 240 5.9% 10%;
    --primary-foreground: 0 0% 98%;
    --secondary: 240 4.8% 95.9%;
    --secondary-foreground: 240 5.9% 10%;
    --muted: 240 4.8% 95.9%;
    --muted-foreground: 240 3.8% 46.1%;
    --accent: 240 4.8% 95.9%;
    --accent-foreground: 240 5.9% 10%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 0 0% 98%;
    --border: 240 5.9% 90%;
    --input: 240 5.9% 90%;
    --ring: 240 5.9% 10%;
    --radius: 0.5rem;

    /* Zone 색상 */
    --zone-ingest: 217 91% 60%;
    --zone-struct: 258 90% 66%;
    --zone-graph: 188 95% 42%;
    --zone-path: 38 92% 50%;

    /* 차트 색상 */
    --chart-success: 142 71% 45%;
    --chart-warning: 38 92% 50%;
    --chart-alert: 0 72% 51%;
    --chart-blue: 217 91% 60%;

    /* Glassmorphism */
    --glass-panel-bg: 0 0% 100% / 0.7;
    --glass-panel-border: 0 0% 100% / 0.2;
    --glass-panel-shadow: 0 0% 0% / 0.05;
  }

  .dark {
    --background: 222 47% 6%;
    --foreground: 0 0% 98%;
    --card: 222 47% 8%;
    --card-foreground: 0 0% 98%;
    --popover: 222 47% 8%;
    --popover-foreground: 0 0% 98%;
    --primary: 0 0% 98%;
    --primary-foreground: 240 5.9% 10%;
    --secondary: 222 30% 14%;
    --secondary-foreground: 0 0% 98%;
    --muted: 222 30% 14%;
    --muted-foreground: 220 10% 55%;
    --accent: 222 30% 14%;
    --accent-foreground: 0 0% 98%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 0 0% 98%;
    --border: 222 20% 16%;
    --input: 222 20% 16%;
    --ring: 217 91% 60%;

    /* Glassmorphism (dark) */
    --glass-panel-bg: 222 40% 10% / 0.6;
    --glass-panel-border: 0 0% 100% / 0.08;
    --glass-panel-shadow: 0 0% 0% / 0.3;
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
  }
}

@layer components {
  .glass-panel {
    background: hsl(var(--glass-panel-bg));
    border: 1px solid hsl(var(--glass-panel-border));
    backdrop-filter: blur(16px) saturate(150%);
    box-shadow: 0 8px 32px hsl(var(--glass-panel-shadow));
    border-radius: var(--radius);
  }
}

/* Stagger 애니메이션 유틸리티 */
@layer utilities {
  .stagger-1 { animation-delay: 0ms; }
  .stagger-2 { animation-delay: 80ms; }
  .stagger-3 { animation-delay: 160ms; }
  .stagger-4 { animation-delay: 240ms; }
}

